import "vitest";
const mockDefaultPrettifiedJsonOutput = "prettyJson";
const mockPrettier = (vi2) => {
  const mock = {
    format: vi2.fn(
      (_data) => new Promise((resolve) => {
        resolve(mockDefaultPrettifiedJsonOutput);
      })
    ),
    resolveConfig: vi2.fn()
  };
  return {
    prettier: mock,
    prettierMock: mock
  };
};
export {
  mockDefaultPrettifiedJsonOutput,
  mockPrettier
};
