const not = (predicate2) => {
  return (value) => !predicate2(value);
};
const equal = (criteria) => {
  return (value) => value === criteria;
};
const and = (...predicates) => {
  return (value) => predicates.every((predicate2) => predicate2(value));
};
const or = (...predicates) => {
  return (value) => predicates.some((predicate2) => predicate2(value));
};
const contains = (criteria) => {
  return (value) => value?.includes(criteria) ?? false;
};
const matchRegExp = (criteria) => {
  return (value) => typeof value === "string" ? criteria.test(value) : false;
};
const predicate = {
  not,
  equal,
  and,
  or,
  contains,
  matchRegExp
};
export {
  and,
  contains,
  equal,
  matchRegExp,
  not,
  or,
  predicate
};
