"use strict";
const remarkGfm = require("remark-gfm");
const remarkLint = require("remark-lint");
const remarkLintListItemIndent = require("remark-lint-list-item-indent");
const remarkLintListItemSpacing = require("remark-lint-list-item-spacing");
const remarkLintNoDuplicateHeadings = require("remark-lint-no-duplicate-headings");
const remarkLintNoDuplicateHeadingsInSection = require("remark-lint-no-duplicate-headings-in-section");
const remarkLintOrderedListMarkerValue = require("remark-lint-ordered-list-marker-value");
const remarkPresetLintConsistent = require("remark-preset-lint-consistent");
const remarkPresetLintMarkdownStyleGuide = require("remark-preset-lint-markdown-style-guide");
const remarkPresetLintRecommended = require("remark-preset-lint-recommended");
const remarkPresetPrettier = require("remark-preset-prettier");
const remarkValidateLinks = require("remark-validate-links");
const unifiedConsistency = require("unified-consistency");
const unifiedPrettier = require("unified-prettier");
const index = {
  settings: { bullet: "-", quote: "'", tightDefinitions: true },
  plugins: [
    remarkLint,
    remarkGfm,
    remarkValidateLinks,
    remarkPresetLintRecommended,
    remarkPresetLintConsistent,
    remarkPresetLintMarkdownStyleGuide,
    [remarkLintListItemSpacing, false, false],
    [remarkLintNoDuplicateHeadings, true],
    [remarkLintListItemIndent, false],
    // Conflicts with prettier
    [remarkLintOrderedListMarkerValue, true, "ordered"],
    remarkLintNoDuplicateHeadingsInSection,
    remarkPresetPrettier,
    unifiedPrettier,
    unifiedConsistency
  ]
};
module.exports = index;
