"use strict";
const vitePluginSvelte = require("@sveltejs/vite-plugin-svelte");
const config = require("vitest/config");
const vitestReporters = ["text", "json", "html", "lcov"];
const vitestCoverage = {
  provider: "v8",
  reporter: vitestReporters,
  reportsDirectory: "./coverage"
};
const vitestWebConfig = {
  plugins: [],
  test: {
    include: ["src/**/*.{test,spec}.{js,mjs,cjs,ts,mts,cts,jsx,tsx}"],
    globals: true,
    environment: "jsdom",
    coverage: vitestCoverage
  }
};
const vitestSvelteConfig = config.mergeConfig(vitestWebConfig, {
  plugins: [vitePluginSvelte.svelte({ hot: !process.env["VITEST"] })]
});
exports.vitestCoverage = vitestCoverage;
exports.vitestSvelteConfig = vitestSvelteConfig;
exports.vitestWebConfig = vitestWebConfig;
