<script generics="T, E">import { onDestroy } from 'svelte';
export let observable;
export let errorType = undefined; // For generic inference only
let completed = false;
let pending = true;
let next;
let error;
let subscription;
$: {
    errorType = undefined; // Just so it's not unused
    completed = false;
    pending = true;
    error = undefined;
    next = undefined;
    if (subscription) {
        subscription.unsubscribe();
    }
    subscription = observable?.subscribe({
        next: (n) => {
            next = n;
            pending = false;
        },
        error: (e) => {
            error = e;
        },
        complete: () => (completed = true),
    });
}
onDestroy(() => subscription?.unsubscribe());
</script>

{#if completed}
	<slot name="completed" last="{next}" />
{:else if error}
	<slot name="error" {error} last="{next}" />
{:else if pending}
	<slot name="pending" />
{:else}
	<slot {next} />
{/if}
